            'passw':null,
            'username':null
        }
    );

    const handleChange = (field) => (e) => {
    setUserData(prev => ({
      ...prev,
      [field]: e.target.value
    }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
