            setResponseMessage(error.message);
        }
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>Sign up</h2>
            <Input label='Email' type='email' value={userData.email} onChange={handleChange} ></Input>
            <Input label='Password' type='password' value={userData.passw} onChange={handleChange}></Input>
